import React, { useState, useEffect } from "react";
import axios from "axios";
import "../Weather.css"; // We'll create this CSS file later

function Weather() {
  const [weatherData, setWeatherData] = useState(null); // Store the fetched data
  const [location, setLocation] = useState(""); // Location input
  const [error, setError] = useState(null); // Store errors (if any)

  // Your OpenWeatherMap API key
  const apiKey = "YOUR_API_KEY"; // Replace with your actual API key

  useEffect(() => {
    // Fetch weather data when component is mounted or location changes
    if (location) {
      fetchWeatherData(location);
    }
  }, [location]);

  const fetchWeatherData = async (location) => {
    try {
      // Fetch current weather data for the location
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}&units=metric`
      );
      setWeatherData(response.data);
      setError(null); // Clear any previous errors
    } catch (err) {
      setError("Error fetching weather data.");
      setWeatherData(null); // Clear previous data
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (location) {
      fetchWeatherData(location);
    }
  };

  return (
    <div className="weather-container">
      <h2 className="weather-title">Weather</h2>

      {/* Search Box */}
      <form onSubmit={handleSearch} className="weather-search-form">
        <input
          type="text"
          placeholder="Enter a city"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="weather-input"
        />
        <button type="submit" className="weather-search-btn">
          Search
        </button>
      </form>

      {/* Show error if there's an issue */}
      {error && <p className="error-message">{error}</p>}

      {/* Display weather information */}
      {weatherData && (
        <div className="weather-info">
          <h3>{weatherData.name}, {weatherData.sys.country}</h3>
          <p>Temperature: {weatherData.main.temp}Â°C</p>
          <p>Conditions: {weatherData.weather[0].description}</p>
          <p>Humidity: {weatherData.main.humidity}%</p>
          <p>Wind Speed: {weatherData.wind.speed} m/s</p>
        </div>
      )}
    </div>
  );
}

export default Weather;
