  try {
    const url = `${baseURL}/recipes/random?apiKey=${apiKey}&number=${number}`;
    const response = await axios.get(url);
    return response.data.recipes;
  } catch (error) {
    console.error("Error fetching random recipes:", error.message);
    throw error;
  }
