import React, {useState} from "react";
import { fetchNasaData } from "../utils/NasaApi";


function Nasa() {
    //State to track user input (Planet) and fetched Nasa data
    const [nasaPlanet, setNasaPlanet] = useState(""); //for user input
    const [nasaData, setNasaData] = useState(null); // For fetched Planet data
    const [error, setError] = useState(null); // For error handling
    const [loading, setLoading] = useState(false); // For loading state

// Handle form submission
const handleFetchData = async (e) => {
    e.preventDefault(); // Prevent page reload
    setError(null); // Clear any previous errors
    setLoading(true); // Indicate data fetching

    try {
      const data = await fetchNasaData(nasaQuery); // Call the API function
      console.log
      setNasaData(data); // Update state with the fetched data
    } catch (err) {
      setError("Failed to fetch data. Please try again.");
    } finally {
      setLoading(false); // Stop loading state
    }
  };

  return (
    <div className="nasa-container">
      <h2>NASA Data Explorer</h2>
      {/* Input Form */}
      <form onSubmit={handleFetchData}>
        <input
          type="text"
          value={nasaPlanet}
          onChange={(e) => setNasaPlanet(e.target.value)}
          placeholder="Enter a NASA query (e.g., planet, topic)"
          className="nasa-input"
        />
        <button type="submit" className="nasa-submit-btn">
          Search
        </button>
      </form>

      {/* Loading State */}
      {loading && <p>Loading...</p>}

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      {/* Fetched Data Display */}
      {nasaData && (
        <div className="nasa-data">
          <h3>Results:</h3>
          <pre>{JSON.stringify(nasaData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default Nasa;
