import React, { useState, useEffect } from "react";
import { fetchWeatherData, fetchWeatherForecast } from "../utils/"; // Import the API functions
import "../Weather.css"; // Optional styling for weather

function Weather() {
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null); // To store forecast data
  const [location, setLocation] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    if (location) {
      fetchWeatherData(location);
      fetchWeatherForecast(location);
    }
  }, [location]);

  const fetchWeatherData = async (location) => {
    try {
      const data = await fetchWeatherData(location);
      setWeatherData(data);
      setError(null); // Reset error state
    } catch (err) {
      setError("Error fetching weather data.");
      setWeatherData(null);
    }
  };

  const fetchWeatherForecast = async (location) => {
    try {
      const forecast = await fetchWeatherForecast(location);
      setForecastData(forecast);
    } catch (err) {
      setError("Error fetching forecast data.");
      setForecastData(null);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (location) {
      fetchWeatherData(location);
      fetchWeatherForecast(location);
    }
  };

  return (
    <div className="weather-container">
      <h2 className="weather-title">Weather</h2>

      <form onSubmit={handleSearch} className="weather-search-form">
        <input
          type="text"
          placeholder="Enter a city"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="weather-input"
        />
        <button type="submit" className="weather-search-btn">
          Search
        </button>
      </form>

      {error && <p className="error-message">{error}</p>}

      {weatherData && (
        <div className="weather-info">
          <h3>{weatherData.name}, {weatherData.sys.country}</h3>
          <p>Temperature: {weatherData.main.temp}°C</p>
          <p>Conditions: {weatherData.weather[0].description}</p>
          <p>Humidity: {weatherData.main.humidity}%</p>
          <p>Wind Speed: {weatherData.wind.speed} m/s</p>
        </div>
      )}

      {forecastData && (
        <div className="weather-forecast">
          <h4>5-Day Forecast:</h4>
          {forecastData.list.slice(0, 5).map((forecast, index) => (
            <div key={index}>
              <p>{new Date(forecast.dt * 1000).toLocaleDateString()} - {forecast.main.temp}°C</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Weather;
