import React, { useState } from "react";
import { fetchRandomRecipes, fetchRecipesByIngredients, fetchMealPlan } from "../utils/foodApi";
import "../Food.css";

function Food() {
  const [mealPlan, setMealPlan] = useState(null);
  const [recipes, setRecipes] = useState([]);
  const [ingredients, setIngredients] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch random recipes
  const handleFetchRandomRecipes = async () => {
    setError(null);
    setLoading(true);
    try {
      const data = await fetchRandomRecipes();
      setRecipes(data);
    } catch (err) {
      setError("Failed to fetch random recipes.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch recipes by ingredients
  const handleFetchRecipesByIngredients = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    try {
      const data = await fetchRecipesByIngredients(ingredients);
      setRecipes(data);
    } catch (err) {
      setError("Failed to fetch recipes by ingredients.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch meal plan
  const handleFetchMealPlan = async () => {
    setError(null);
    setLoading(true);
    try {
      const data = await fetchMealPlan();
      setMealPlan(data);
    } catch (err) {
      setError("Failed to fetch meal plan.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="food-container">
      <h2>Meal Planner App</h2>

      <div className="actions">
        <button onClick={handleFetchRandomRecipes}className="weather-search-btn">Get Random Recipes</button>
        <button onClick={handleFetchMealPlan}className="mealplan-search-btn">Generate Meal Plan</button>
      </div>

      <form onSubmit={handleFetchRecipesByIngredients}>
        <input
          type="text"
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          placeholder="Enter ingredients (comma-separated)"
          className="ingredient-input"
        />
        <button type="submit" className="ingredient-search-btn">Search Recipes by Ingredients</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Display random recipes */}
      {recipes.length > 0 && (
        <div className="recipes">
          <h3>Recipe Suggestions</h3>
          {recipes.map((recipe) => (
            <div key={recipe.id} className="recipe">
              <h4>{recipe.title}</h4>
              <img src={recipe.image} alt={recipe.title} />
              <p>
                <a href={`https://spoonacular.com/recipes/${recipe.title}-${recipe.id}`} target="_blank" rel="noopener noreferrer">
                  View Recipe
                </a>
              </p>
            </div>
          ))}
        </div>
      )}

      {/* Display meal plan */}
      {mealPlan && (
        <div className="meal-plan">
          <h3>Daily Meal Plan</h3>
          <ul>
            {mealPlan.meals.map((meal) => (
              <li key={meal.id}>
                {meal.title} - <a href={meal.sourceUrl} target="_blank" rel="noopener noreferrer">View Recipe</a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Food;
